<?phpnamespace AtlantisGroup\OrmAGBundle\DependencyInjection;use Symfony\Component\Config\Definition\Builder\ArrayNodeDefinition;use Symfony\Component\Config\Definition\Builder\TreeBuilder;use Symfony\Component\Config\Definition\ConfigurationInterface;class Configuration implements ConfigurationInterface{    protected $name = 'orm_ag';    public function getConfigTreeBuilder()    {        $treeBuilder = new TreeBuilder('orm_model');var_dump($treeBuilder);        /** @var ArrayNodeDefinition $rootNode */        $rootNode = method_exists($treeBuilder, 'getRootNode') ? $treeBuilder->getRootNode() : 'orm_model';        //$rootNode = $treeBuilder->getRootNode();var_dump($rootNode);        $rootNode            ->children()                ->arrayNode('connection')                    ->children()                        ->scalarNode('server')->defaultValue('')->end()                        ->scalarNode('user')->defaultValue('')->end()                        ->scalarNode('pass')->defaultValue('')->end()                        ->scalarNode('database')->defaultValue('')->end()                        ->scalarNode('charset')->defaultValue('utf8')->end()                    ->end()                ->end()            ->end();        return $treeBuilder;    }}