<?phpnamespace AtlantisGroup\OrmAGBundle\DependencyInjection;use Symfony\Component\Config\Definition\Builder\ArrayNodeDefinition;use Symfony\Component\Config\Definition\Builder\TreeBuilder;use Symfony\Component\Config\Definition\ConfigurationInterface;class Configuration implements ConfigurationInterface{    /**     * @return TreeBuilder     */    public function getConfigTreeBuilder(): TreeBuilder    {        $treeBuilder = new TreeBuilder('orm_ag');        /** @var ArrayNodeDefinition $rootNode */        //$rootNode = method_exists($treeBuilder, 'getRootNode') ? $treeBuilder->getRootNode() : 'orm_ag';        $rootNode = $treeBuilder->getRootNode();        $rootNode            ->children()                ->arrayNode('connection')                    ->children()                        ->scalarNode('server')->defaultValue('')->end()                        ->scalarNode('user')->defaultValue('')->end()                        ->scalarNode('pass')->defaultValue('')->end()                        ->scalarNode('port')->defaultValue('3306')->end()                        ->scalarNode('database')->defaultValue('')->end()                        ->scalarNode('charset')->defaultValue('utf8')->end()                    ->end()                ->end()            ->end();        return $treeBuilder;    }}