<?phpnamespace AtlantisGroup\OrmAG\Connection;use Psr\Log\LoggerInterface;/** * Interface ConnectionFactoryInterface * @package AtlantisGroup\OrmAG\Connection */interface ConnectionFactoryInterface{    /**     * @param string $host     * @param string $user     * @param string $pass     * @param string $port     * @param string $database     * @param string $charset     * @param LoggerInterface $logger     */    public function __construct(string $host, string $user, string $pass, string $port, string $database, string $charset, LoggerInterface $logger);    /**     * @param string $query     * @param array $bind     */    public function fetchAll(string $query, array $bind = []);    /**     * @param string $query     */    public function fetchAllNoBind(string $query);}