<?phpnamespace AtlantisGroup\OrmAG\Connection;use mysqli;use mysqli_stmt;use Psr\Log\LoggerInterface;class MySql implements MySqlInterface{    /**     * @var string     */    private string $host;    /**     * @var string     */    private string $user;    /**     * @var string     */    private string $pass;    /**     * @var string     */    private string $port;    /**     * @var string     */    private string $dbname;    /**     * @var string     */    private string $charset;    /**     * @var LoggerInterface     */    private LoggerInterface $logger;    public function __construct(string $host, string $user, string $pass, string $port, string $database, string $charset, LoggerInterface $logger)    {        $this->host = $host;        $this->user = $user;        $this->pass = $pass;        $this->port = $port;        $this->dbname = $database;        $this->charset = $charset;        $this->logger = $logger;    }    protected function getAll()    {    }    /**     * @return mysqli     */    private function setConnection(): mysqli    {        return new mysqli($this->host, $this->user, $this->pass, $this->dbname, $this->port, $this->charset);    }}