<?phpnamespace AtlantisGroup\OrmAG\DataAccess;use Psr\Log\LoggerInterface;/** * Interface DataAccessInterface * @package AtlantisGroup\OrmAG\DataAccess */interface DataAccessInterface{    /**     * @param string $field     * @param string|null $where     * @param array $bind     * @return mixed     */    public function sum(string $field, string $where = null, array $bind = []): mixed;    /**     * @param LoggerInterface|null $logger     * @return DataAccessInterface     */    public function setLogger(LoggerInterface $logger = null): DataAccessInterface;    /**     * @param bool $debug     * @return DataAccessInterface     */    public function setDebug(bool $debug): DataAccessInterface;}