<?phpnamespace AtlantisGroup\OrmAG\DataMapper;use AtlantisGroup\OrmAG\DataAccess\DataAccessInterface;use AtlantisGroup\OrmAG\Service\ServiceInterface;/** * Interface DataMapperInterface * @package AtlantisGroup\OrmAG\DataMapper */interface DataMapperInterface{    /**     * @return string     */    public function getEntityClassName(): string;    /**     * @param DataAccessInterface $dataAccess     * @return DataMapperInterface     */    public function setDataAccess(DataAccessInterface $dataAccess): DataMapperInterface;    /**     * @param ServiceInterface $service     * @return DataMapperInterface     */    public function setService(ServiceInterface $service): DataMapperInterface;    /**     * @param string $field     * @param string|null $where     * @param array $bind     * @return mixed     */    public function sum(string $field, string $where = null, array $bind = []): mixed;}