<?phpnamespace AtlantisGroup\OrmAG\Service;use AtlantisGroup\OrmAG\DataAccess\DataAccessInterface;use Symfony\Component\Stopwatch\Stopwatch;/** * Class AbstractService * @package AtlantisGroup\OrmAG\Service */abstract class AbstractService implements ServiceInterface{    /**     * @var DataAccessInterface     */    protected DataAccessInterface $dataAccess;    /**     * @var Stopwatch     */    protected Stopwatch $stopwatch;    /**     * @var bool     */    protected bool $debug;    public function __construct(DataAccessInterface $dataAccess, bool $debug = false)    {        $this->dataAccess = $dataAccess;        $this->debug = $debug;        $this->dataAccess->setDebug($this->debug);    }    /**     * @param Stopwatch $stopwatch     * @return AbstractService     */    public function setStopwatch(Stopwatch $stopwatch): ServiceInterface    {        $this->stopwatch = $stopwatch;        return $this;    }    /**     * @param bool $debug     * @return ServiceInterface     */    public function setDebug(bool $debug): ServiceInterface    {        $this->debug = $debug;        $this->dataAccess->setDebug($debug);        return $this;    }}