<?phpnamespace AtlantisGroup\OrmAG\Service;use Symfony\Component\Stopwatch\Stopwatch;/** * Interface ServiceInterface * @package AtlantisGroup\OrmAG\Service */interface ServiceInterface{    /**     * @param string $field     * @param string|null $where     * @param array $bind     * @return mixed     */    public function sum(string $field, string $where = null, array $bind = []): mixed;    /**     * @param Stopwatch $stopwatch     * @return AbstractService     */    public function setStopwatch(Stopwatch $stopwatch): ServiceInterface;    /**     * @param bool $debug     * @return ServiceInterface     */    public function setDebug(bool $debug): ServiceInterface;}